import React, { Fragment, Component } from 'react';
import Navbar from './components/layout/Navbar';
import Users from './components/users/Users';
import Search from './components/users/Search';

import axios from 'axios';
import './App.css';

// Function turned into a class.  Extends component class
class App extends Component {
  state = {
    users: [],
    loading: false
  }


  async componentDidMount() {
    console.log(process.env.REACT_APP_GITHUB_CLIENT_SECRET);

    this.setState({ loading: true });


    const res = await axios.get(`https://api.github.com/users?client_id=$
      {process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=$
      {process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);

    this.setState({ users: res.data, loading: false });
  }

  // Search Github users
  // Since is an arrow functin, call async before the parameter
  searchUsers = async text => {
    const res = await axios.get(`https://api.github.com/search/users?q={text}&client_id=$
    {process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=$
    {process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);

    this.setState({ users: res.data.items, loading: false });
  }


  render() {
    // Looks like html but actually JSX (Javascript Syntax Extension)
    return (
      <div className="App">
        <Navbar />
        <div className='container'>
          <Search searchUsers={this.searchUsers} />
          <Users loading={this.state.loading} users={this.state.users} />
        </div>
      </div>
    );

    // // Using straight Javascript
    // return React.createElement('div', { className: 'App' }, React.createElement('h1', null, 'Hello from React')
    // );
  }
}

export default App;
