import React, { Component, Fragment } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Users from './components/users/Users';
import Search from './components/users/Search';
import Alert from './components/layout/Alert';

import axios from 'axios';
import './App.css';

// Function turned into a class.  Extends component class
class App extends Component {
  state = {
    users: [],
    loading: false,
    alert: null
  };


  // async componentDidMount() {
  //   console.log(process.env.REACT_APP_GITHUB_CLIENT_SECRET);

  //   this.setState({ loading: true });


  //   const res = await axios.get(`https://api.github.com/users?client_id=$
  //     {process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=$
  //     {process.env.REACT_APP_GITHUB_CLIENT_SECRET}`);

  //   this.setState({ users: res.data, loading: false });
  // };


  // Search Github users
  // Since is an arrow functin, call async before the parameter
  searchUsers = async text => {
    this.setState({ loading: true });

    const res = await axios.get(
      `https://api.github.com/search/users?q=eric&client_id=
      ${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=
      ${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
    );

    this.setState({ users: res.data.items, loading: false });
  };

  // Clear users from state
  clearUsers = () => this.setState({ users: [], loading: false })

  // Set Alert
  setAlert = (msg, type) => {
    this.setState({ alert: { msg, type }});

    setTimeout(() => this.setState({ alert: null }), 5000);
  };


  render() {
    const { users, loading } = this.state;

    return (
      <Router>
        <div className="App">
          <Navbar />
          <Alert alert={ this.state.alert } />
          <Switch>
            <Route exact path='/' render={props => (
              <Fragment>
                <Search 
                  searchUsers={ this.searchUsers } 
                  clearUsers={ this.clearUsers } 
                  showClear={ users.length > 0 ? true : false }
                  setAlert={ this.setAlert }
                />
                <Users loading={ loading } users={ users } />
              </Fragment>
            )} />
          </Switch>
          <div className='container'>

          </div>
        </div>
      </Router>
    );

    // // Using straight Javascript
    // return React.createElement('div', { className: 'App' }, React.createElement('h1', null, 'Hello from React')
    // );
  }
}

export default App;
